import React from 'react';
import {Link} from 'react-router-dom';
import './bookdisplay.css';
import cchcover from './cchcover.jpg';

function FeaturedNovel(props) {
    return (
        <>
            {/* <h1 className='display'>Book Display</h1> */}
            <ul>
                <Link to={props.path}>
                    <div>
                        <div>
                        <figure data-category={props.label}>
                            <img src={props.src} alt="Cross Country Hearts"
                            className="novel__image"/>
                        </figure>
                        </div>
                        <div>
                            <h5>{props.text1}</h5>
                            {/* <p>{props.text2}</p>
                            <p>{props.text3}</p>
                            <p>{props.text4}</p>
                            <p>{props.text5}</p> */}
                        </div>
                    </div>
                </Link>
            </ul>
        </>
    )
}

export default FeaturedNovel;
