import React from 'react';
// import '../App.css';
import {Button} from '../Button';
// import './Novellas.css';
import dfcover from './Final Defining Life Cover.png';
import comingsoon from './Coming Soon.png'
import FeaturedBook from './bookdisplay';

function Novellas() {

    return (
        <>
        <div className="novellas__section">
            <h1>Novellas</h1>
            <section class="first">
                <FeaturedBook 
                    src={dfcover}
                    text1="Her sister is the bride. His cousin is the groom. 
                        This love story involves two enemies, one car, and an epic road trip."
                    label="book"
                    path="/"
                />
            </section>
            <section class="second">
                <FeaturedBook 
                    src={comingsoon}
                    text1="Her sister is the bride. His cousin is the groom. 
                        This love story involves two enemies, one car, and an epic road trip."
                    label="book"
                    path="/"
                />
            </section>
        </div>
        </>
    )
}

export default Novellas;
