import React from 'react';
// import '../App.css';
import {Button} from '../Button';
import {Link} from 'react-router-dom';
import './bookdisplay.css';
import FeaturedNovel from './noveldisplay';
import cchcover from './cchcover.jpg';
import dfcover from './Final Defining Life Cover.png';
import comingsoon from './Coming Soon.png';

function Novels() {
    return (
        <>
        <div className='novel__display'>
        <h1 className='novel__title'>NOVELS</h1>
        <section>
            <div className='novel__left'>
                <FeaturedNovel
                src={cchcover}
                text1="Cross Country Hearts!"
                label="book"
                path="/"
                />
            </div>
            <div className='novel__left'>
                <FeaturedNovel
                src={dfcover}
                text1="Cross Country Hearts!"
                label="book"
                path="/"
                />
            </div>
            <div className='novel__left'>
                <FeaturedNovel
                src={comingsoon}
                text1="Check out Cross Country Hearts!"
                label="book"
                path="/"
                />
            </div>
        </section>
    </div>
        </>
    )
}

export default Novels;
