import React from 'react';
// import '../App.css';
import {Button} from '../Button';
import {Link} from 'react-router-dom';
import './bookdisplay.css';
import FeaturedBook from './bookdisplay';
import cchcover from './cchcover.jpg';

function Novellas() {
    return (
        <>
        <h1 className='novel-display'>Novel Display</h1>
        <div className='cards'>
            <h1>Check out Cross Country Hearts</h1>
            <div className='cards__container'>
                <div className='cards__wrapper'>
                    <ul className='cards__items'>
                        <FeaturedBook 
                        src={cchcover}
                        text="Check out Cross Country Hearts!"
                        label="book"
                        path="/"
                        />
                    </ul>
                </div>
            </div>
        </div>
        </>
    )
}

export default Novellas;
