import React from 'react';
import {Link} from 'react-router-dom';
import './bookdisplay.css';
import cchcover from './cchcover.jpg';

function FeaturedBook(props) {
    return (
        <>
            {/* <h1 className='display'>Book Display</h1> */}
            <li className='cards__item'>
                <Link to={props.path} className='cards__item__link'>
                    <p>Responsive div using css.</p>
                    <div class="first">
                        <p>First block</p>
                    </div>
                    <div class="second">
                        <p>Second block</p>
                    </div>
                    <div class="third">
                        <p>Third block </p>
                    </div>
                    <p>
                        The media query will only apply 
                        if the media type is screen
                        and the viewport is equal to 
                        or less than 500px
                    </p>
                </Link>
            </li>
        </>
    )
}

export default FeaturedBook;
