import React from 'react';
import {Link} from 'react-router-dom';
import './BookDisplay.css';
import cchcover from './cchcover.jpg';

function FeaturedBook(props) {
    return (
        <>
            {/* <h1 className='display'>Book Display</h1> */}
            <ul>
                <Link to={props.path} >

                    <div className="book__display">
                        <div className="left">
                        <figure data-category={props.label}>
                            <img src={props.src} alt="Cross Country Hearts"
                            className="book__img"/>
                        </figure>
                        </div>
                        <div className="right">
                            <h5>{props.text1}</h5>
                            <h5>{props.text2}</h5>
                            <h5>{props.text3}</h5>
                            <h5>{props.text4}</h5>
                            <h5>{props.text5}</h5>
                        </div>
                    </div>
                </Link>
            </ul>
        </>
    )
}

export default FeaturedBook;
