import React, {useState} from 'react';
import {Link} from 'react-router-dom';

function NavBar(){

    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);

    const closeMobileMenu = () => setClick(false);

    return(
        <>
            <nav className='navbar'>
                <div className='navbar-container'>
                    <Link to='/' className='navbar-logo'>
                        Home <i className='fab fa-typo3' />
                    </Link>
                    {/* <Link to='/books' className='navbar-logo'>
                        Books <i className='fab fa-typo3' />
                    </Link>
                    <Link to='/about' className='navbar-logo'>
                        About <i className='fab fa-typo3' />
                    </Link>
                    <Link to='/media' className='navbar-logo'>
                        Media <i className='fab fa-typo3' />
                    </Link> */}
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className='nav-item'>
                            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                                Home
                            </Link>
                        </li>
                    </ul>
                </div>
            </nav>
        </>
    );
}

export default NavBar;
